import React from "react"



import TopMenu from './TopMenu.js'
import BackMenu from './BackMenu.js'
import BackMenuItem from './BackMenuItem.js'

import { makeStyles } from '@material-ui/core/styles';
import Paper from '@material-ui/core/Paper';
import Grid from '@material-ui/core/Grid';
import Box from '@material-ui/core/Box';
import Typography from '@material-ui/core/Typography';
import IconButton from '@material-ui/core/IconButton';
import MenuIcon from '@material-ui/icons/Menu';
import Fab from '@material-ui/icons/Menu';

import { AcUnit } from '@material-ui/icons'
import { ArrowBackIos } from '@material-ui/icons'
import { OndemandVideo } from '@material-ui/icons'
import { Add } from '@material-ui/icons'
import { PhotoCamera } from '@material-ui/icons'
import { WifiTethering } from '@material-ui/icons'




const useStyles = makeStyles(theme => ({
  paper: {
    padding: theme.spacing(2),
    textAlign: 'center',
    color: theme.palette.text.secondary,

    color: 'red',
    boxShadow: 'none',
    borderBottom: '1px solid red',
    borderRadius: '0px',
  },
  grid_logotype: {
    display: 'flex',
    justifyContent: 'space-between',
    alignItems: 'center',
    paddingLeft: theme.spacing(4),
    paddingRight: theme.spacing(4),
    borderBottom: '1px solid red',

    height: '8vh'
  },
  row: {
    display: 'flex',
    justifyContent: 'flex-start',
    alignItems: 'center',
  },
  iconSpacing:{
    marginRight: theme.spacing(2),
  },
  title: {
    color: theme.palette.text.secondary,
  },
  gridInnerPadding: {
    height: '92vh',
    borderRight: '1px solid red',
    paddingTop: theme.spacing(6),
    paddingLeft: theme.spacing(4),
    paddingRight: theme.spacing(4),

    position: 'fixed'
  },
}));



export default function App(){
  const classes = useStyles();

  return(
    <div className={classes.root}>
      <Grid container>
        <Grid item xs={3} className={classes.grid_logotype}>

        </Grid>
        <Grid item xs={9}>
          <TopMenu />
        </Grid>









        <Grid item xs={3} className={classes.gridInnerPadding}>

          <BackMenuItem>
            <OndemandVideo fontSize='large' className={classes.iconSpacing}/>
            <Typography variant="h5">
              Videos
            </Typography>
          </BackMenuItem>

          <BackMenuItem>
            <PhotoCamera fontSize='large' className={classes.iconSpacing}/>
            <Typography variant="h5">
              Photos
            </Typography>
          </BackMenuItem>

          <BackMenuItem>
            <WifiTethering fontSize='large' className={classes.iconSpacing}/>
            <Typography variant="h5">
              Broadcasts
            </Typography>
          </BackMenuItem>

        </Grid>



        <Grid item xs={12} style={{height: '120vh'}}>

        </Grid>











      </Grid>
    </div>
  )
}
