import React from "react"
import { useState } from 'react'
import { connect } from 'react-redux'


import TopMenu from './TopMenu.js'
import BackMenu from './BackMenu.js'
import BackMenuItem from './BackMenuItem.js'

import { makeStyles } from '@material-ui/core/styles';

import Paper from '@material-ui/core/Paper';
import Grid from '@material-ui/core/Grid';
import Box from '@material-ui/core/Box';
import Chip from '@material-ui/core/Chip';
import Select from '@material-ui/core/Select';
import MenuItem from '@material-ui/core/MenuItem';
import Typography from '@material-ui/core/Typography';
import IconButton from '@material-ui/core/IconButton';
import Input from '@material-ui/core/Input';
import FormControl from '@material-ui/core/FormControl';
import InputAdornment from '@material-ui/core/InputAdornment';
import InputLabel from '@material-ui/core/InputLabel';
import Fab from '@material-ui/core/Fab';
import { MuiPickersUtilsProvider, KeyboardDatePicker } from '@material-ui/pickers';
import DateFnsUtils from '@date-io/date-fns';
import {
  BrowserRouter as Router,
  Switch,
  Route,
  Link
} from "react-router-dom";


import MenuIcon from '@material-ui/icons/Menu';
import { AcUnit } from '@material-ui/icons'
import { ArrowBackIos } from '@material-ui/icons'
import { OndemandVideo } from '@material-ui/icons'
import { Add } from '@material-ui/icons'
import { PhotoCamera } from '@material-ui/icons'
import { WifiTethering } from '@material-ui/icons'
import { ArrowBackIosOutlined } from '@material-ui/icons'
import { SearchOutlined } from '@material-ui/icons'

import Videos from './Videos'
import Photos from './Photos'
import Broadcasts from './Broadcasts'



const names = [
  'Oliver Hansen',
  'Van Henry',
  'April Tucker',
  'Ralph Hubbard',
  'Omar Alexander',
  'Carlos Abbott',
  'Miriam Wagner',
  'Bradley Wilkerson',
  'Virginia Andrews',
  'Kelly Snyder',
];



function App(props){
  const selectedDate = '10/21/1991'
  function handleDateChange(){
    alert(1)
  }


  const useStyles = makeStyles(theme => ({
    paper: {
      padding: theme.spacing(2),
      textAlign: 'center',
      color: theme.palette.text.secondary,

      color: 'red',
      boxShadow: 'none',
      borderBottom: '1px solid red',
      borderRadius: '0px',
    },
    grid_logotype: {
      display: 'flex',
      justifyContent: 'space-between',
      alignItems: 'center',
      paddingLeft: theme.spacing(3),
      paddingRight: theme.spacing(3),
      borderBottom: '1px solid #ccc',

      height: '8vh'
    },
    row: {
      display: 'flex',
      justifyContent: 'flex-start',
      alignItems: 'center',
    },
    iconSpacing:{
      marginRight: theme.spacing(2),
      color: '#333'
    },
    title: {
      color: theme.palette.text.secondary,
    },
    gridInnerPadding: {


      paddingTop: theme.spacing(6),
      paddingLeft: theme.spacing(4),
      paddingRight: theme.spacing(4),
    },
    backMenu: {
      paddingTop: theme.spacing(4),
      borderRight: '1px solid #ccc',
      height: '92vh',
    },

    flexRow: {
      display: 'flex',
    },

    sideMenu: {
      position: 'fixed',
      display: props.isLeftMenu ? 'block' : 'none'
    },
    contentOutput: {
      height: '200vh'
    },
    grey: {
      color: '#333'
    },
    centered: {
      display: 'flex',
      justifyContent: 'center',
      alignItems: 'center'
    },
    bottomed: {
      display: 'flex',
      justifyContent: 'center',
      alignItems: 'flex-end'
    },
    avoidMargin: {
      margin: 0
    },
    margined: {
      marginBottom: theme.spacing(1)
    }
  }));


  const classes = useStyles();

  const [personName, setPersonName] = useState([])
  const handleChange = event => {
    setPersonName(event.target.value);
  };

  return(
    <Router>
    <div className={classes.root}>


      <Grid container>
        <Grid container className={classes.sideMenu}>
          <Grid item xs={3}>

            <Grid item xs={12} className={classes.grid_logotype}>


              <BackMenuItem title='ISSF-TV' stable>
                <OndemandVideo fontSize='large' className={classes.iconSpacing}/>
              </BackMenuItem>
            </Grid>


            <Grid item xs={12} className={classes.backMenu}>
              <Link to='/videos'>
              <BackMenuItem title='Videos'>
                <OndemandVideo fontSize='large' className={classes.iconSpacing}/>
              </BackMenuItem>
              </Link>

              <Link to='/photos'>
              <BackMenuItem title='Photos'>
                <PhotoCamera fontSize='large' className={classes.iconSpacing}/>
              </BackMenuItem>
              </Link>

              <Link to='/broadcasts'>
              <BackMenuItem title='Broadcasts'>
                <WifiTethering fontSize='large' className={classes.iconSpacing}/>
              </BackMenuItem>
              </Link>

            </Grid>
          </Grid>
        </Grid>




        { props.isLeftMenu && <Grid item xs={3}></Grid> }

        <Grid item xs={props.lenLeftMenu}>

          <Grid item xs={12}>
            <TopMenu />
          </Grid>


          <Grid container className={classes.backMenu}>

            { !props.isLeftMenu &&
            <Grid item xs={1}>
              <Link to='/videos' >
                <BackMenuItem>
                  <OndemandVideo fontSize='large' className={classes.grey}/>
                </BackMenuItem>
              </Link>
              <Link to='/photos' >
              <BackMenuItem>
                <PhotoCamera fontSize='large' className={classes.grey}/>
              </BackMenuItem>
              </Link>
              <Link to='/broadcasts' >
              <BackMenuItem>
                <WifiTethering fontSize='large' className={classes.grey}/>
              </BackMenuItem>
              </Link>
            </Grid>
            }


            <Grid item xs={11} className={classes.contentOutput}>

            <Grid item xs={12}>
              <FormControl fullWidth className={classes.avoidMargin}>
                <Input
                  id='search_by_name_input'
                  name='video["name"]'
                  placeholder="Search by name"
                  className={classes.input}
                  inputProps={{
                  'aria-label': 'description',
                  }}
                  startAdornment={<InputAdornment position="start"><SearchOutlined /></InputAdornment>}
                />
                </FormControl>
              </Grid>

              <Grid container>
              <Grid item className={classes.centered}>
                <Fab color="secondary" aria-label="edit" className={classes.fab}>
                  <SearchOutlined />
                </Fab>
              </Grid>

              <Grid item className={classes.bottomed}>
              <MuiPickersUtilsProvider utils={DateFnsUtils}>
                <KeyboardDatePicker
                  disableToolbar
                  variant="inline"
                  format="MM/dd/yyyy"
                  margin="normal"
                  id="date-from"
                  label="From"
                  value={selectedDate}
                  onChange={handleDateChange}
                  KeyboardButtonProps={{
                    'aria-label': 'change date',
                  }}
                />
                <KeyboardDatePicker
                  disableToolbar
                  variant="inline"
                  format="MM/dd/yyyy"
                  margin="normal"
                  id="date-to"
                  label="To"
                  value={selectedDate}
                  onChange={handleDateChange}
                  KeyboardButtonProps={{
                    'aria-label': 'change date',
                  }}
                />                
              </MuiPickersUtilsProvider>
              </Grid>




                  <Grid item style={{flexGrow: 1}} className={classes.bottomed}>
                  <FormControl fullWidth className={classes.margined}>
                    <InputLabel id="demo-mutiple-chip-label">Categories</InputLabel>
                    <Select
                      labelId="demo-mutiple-chip-label"
                      id="demo-mutiple-chip"
                      multiple
                      value={personName}
                      onChange={handleChange}
                      input={<Input id="select-multiple-chip" />}
                      renderValue={selected => (
                        <div>
                          {selected.map(value => (
                            <Chip key={value} label={value} className={classes.chip} />
                          ))}
                        </div>
                      )}
                    >
                      {names.map(name => (
                        <MenuItem key={name} value={name}>
                          {name}
                        </MenuItem>
