import React from 'react'
import { useRef } from 'react'

import { makeStyles } from '@material-ui/core/styles';

import Box from '@material-ui/core/Box';
import Typography from '@material-ui/core/Typography';



const useStyles = makeStyles(theme => ({
    row: {
        display: 'flex',
        justifyContent: 'flex-start',
        alignItems: 'center',
        padding: theme.spacing(3),
    }
}))


export default function BackMenuItem({children, title}){
    const classes = useStyles()
    const linkRef = useRef(null)

    return(
        <Box className={classes.row} ref={linkRef} onMouseEnter={highlight} onMouseLeave={darken} >
            {children}
            <Typography variant="h4" color="textSecondary" className={classes.backMenuTitle}>
                {title}
            </Typography>
        </Box>

    )
}


//handlers
function highlight(){
    alert('test')
}
