import React from "react"



import TopMenu from './TopMenu.js'
import BackMenu from './BackMenu.js'

import { makeStyles } from '@material-ui/core/styles';
import Paper from '@material-ui/core/Paper';
import Grid from '@material-ui/core/Grid';
import Box from '@material-ui/core/Box';
import Typography from '@material-ui/core/Typography';
import IconButton from '@material-ui/core/IconButton';
import MenuIcon from '@material-ui/icons/Menu';
import Fab from '@material-ui/icons/Menu';

import { AcUnit } from '@material-ui/icons'
import { ArrowBackIos } from '@material-ui/icons'
import { OndemandVideo } from '@material-ui/icons'
import { Add } from '@material-ui/icons'



const useStyles = makeStyles(theme => ({
  paper: {
    padding: theme.spacing(2),
    textAlign: 'center',
    color: theme.palette.text.secondary,

    color: 'red',
    boxShadow: 'none',
    borderBottom: '1px solid red',
    borderRadius: '0px',
  },
  grid_logotype: {
    display: 'flex',
    justifyContent: 'space-between',
    alignItems: 'center',
    paddingLeft: theme.spacing(4),
    paddingRight: theme.spacing(4),
    borderBottom: '1px solid red',

    height: '8vh'
  },
  row: {
    display: 'flex',
    justifyContent: 'flex-start',
    alignItems: 'center',
  },
  logotype:{
    marginRight: theme.spacing(2),
  },
  title: {
    color: theme.palette.text.secondary,
  },
  test: {
    height: '92vh',
    borderRight: '1px solid red',
    paddingTop: theme.spacing(6),
    paddingLeft: theme.spacing(4),
    paddingRight: theme.spacing(4),
  },
  fab: {
    margin: theme.spacing(1),
  },
}));



export default function App(){
  const classes = useStyles();

  return(
    <div className={classes.root}>
      <Grid container>
        <Grid item xs={3} className={classes.grid_logotype}>
              <Box className={classes.row}>
                <AcUnit fontSize='large' className={classes.logotype}/>
                <Typography variant="h4" className={classes.title}>
                  ISSF-TV
                </Typography>
              </Box>
              <Box>


                <IconButton edge="start" className={classes.menuButton} color="inherit" aria-label="menu">
                  <ArrowBackIos fontSize='default' />
                </IconButton>
              </Box>
        </Grid>


        <Grid item xs={9}>
          <TopMenu />
        </Grid>

        <Grid item xs={3} className={classes.test}>
          <Box>
            <OndemandVideo fontSize='large' />1
          </Box>
          <Box>
          <AcUnit fontSize='large' />2
          </Box>
          <Fab color="primary" aria-label="add" className={classes.fab}>
            <Add />
          </Fab>
        </Grid>

      </Grid>
    </div>
  )
}
