import React from 'react'
import { useState } from 'react'

import { makeStyles } from '@material-ui/core/styles';

import Box from '@material-ui/core/Box';
import Typography from '@material-ui/core/Typography';






export default function BackMenuItem({children, title}){

    const [color, setColor] = useState('none')

    const useStyles = makeStyles(theme => ({
        row: {
            display: 'flex',
            justifyContent: title != undefined ? 'flex-start' : 'center',
            alignItems: 'center',
            padding: theme.spacing(3),
            backgroundColor: color
        },
        backMenuTitle: {
            color: '#ccc'
        }
    }))



    function highlight(){
        setColor('#ccc')
    }


    function darken(){
        setColor('none')
    }


    const classes = useStyles()

    return(
        <Box className={classes.row} onMouseEnter={highlight} onMouseLeave={darken} >
            {children}
            { title != undefined &&
                <Typography variant="h5" className={classes.backMenuTitle}>
                    {title}
                </Typography>
            }
        </Box>

    )
}
